/* eslint-disable no-unused-vars */

export function desc(a, b, orderBy) {
    if (b[orderBy] < a[orderBy]) {
      return -1;
    }
    if (b[orderBy] > a[orderBy]) {
      return 1;
    }
    return 0;
  }
  
 export function stableSort(array, cmp) {
    // const stabilizedThis = array.slice((el, index) => [el, index]);
